// Get trigger timestamp
const triggerTimestamp = $('Schedule Trigger').first().json.timestamp;
const now = new Date(triggerTimestamp);
const startDate = new Date(now);
startDate.setDate(now.getDate() - 7);

// Format dates for Square API (RFC3339 format)
const startDateStr = startDate.toISOString();
const endDateStr = now.toISOString();

// Get location ID from previous node
const locationId = $input.item.json.id;
const locationName = $input.item.json.name || 'Unknown';

// Build the API payload WITHOUT return_entries to get full order data
const payloadObj = {
  location_ids: [locationId],
  query: {
    filter: {
      date_time_filter: {
        created_at: {
          start_at: startDateStr,
          end_at: endDateStr
        }
      },
      state_filter: {
        states: ["COMPLETED"]
      }
    },
    sort: {
      sort_field: "CREATED_AT",
      sort_order: "DESC"
    }
  },
  limit: 500
};

// Return the data with payload as string
return {
  json: {
    locationId: locationId,
    locationName: locationName,
    payloadString: JSON.stringify(payloadObj),
    startDate: startDateStr,
    endDate: endDateStr
  }
};

// ============================================
// DETAILED SALES REPORT FROM ORDERS
// ============================================

// 1. Get orders directly from the API response
const orders = $json.orders || [];

console.log('=== PROCESSING ORDERS ===');
console.log('Total orders:', orders.length);

if (orders.length === 0) {
  return [{
    json: {
      message: 'No orders found'
    }
  }];
}

// 2. Get date range
const triggerDate = $('Schedule Trigger').first().json.timestamp;
const endDate = new Date(triggerDate);
const startDate = new Date(triggerDate);
startDate.setDate(startDate.getDate() - 7);

// 3. Get location info
const location_id = orders[0].location_id || null;
let location_name = 'Unknown Location';
try {
  const splitLocationItem = $('Split Locations').item.json;
  location_name = splitLocationItem.name || splitLocationItem.id || 'Unknown Location';
} catch (e) {
  location_name = location_id || 'Unknown Location';
}

// Log first order to verify structure
if (orders.length > 0) {
  console.log('First order sample:', JSON.stringify(orders[0], null, 2));
}

// ============================================
// 4. Process each order individually
// ============================================
const detailedReports = [];

for (const order of orders) {
  if (!order || !order.id) continue;

  // Extract basic info
  const order_id = order.id;
  const order_date = order.created_at ? order.created_at.split('T')[0] : '';
  const order_time = order.created_at ? order.created_at.split('T')[1]?.substring(0, 8) : '';

  // Line items
  const line_items = order.line_items || [];
  const items_description = line_items.map(item => 
    `${item.name || 'Unknown'} (${item.quantity || 1})`
  ).join('; ');

  // Financial data (convert cents to dollars)
  const total_money = (order.total_money?.amount || 0) / 100.0;
  const total_tax = (order.total_tax_money?.amount || 0) / 100.0;
  const total_discount = Math.abs((order.total_discount_money?.amount || 0)) / 100.0;
  const total_tip = (order.total_tip_money?.amount || 0) / 100.0;
  const cash_rounding = (order.rounding_adjustment?.amount_money?.amount || 0) / 100.0;

  // Calculate net sales
  const net_sales = total_money - total_tip - total_tax - cash_rounding;
  const gross_sales = net_sales - total_discount;

  // Tenders (payment methods)
  const tenders = order.tenders || [];
  let cash = 0;
  let card = 0;
  let gift_card = 0;
  let other = 0;
  let fees = 0;

  for (const tender of tenders) {
    const amount = (tender.amount_money?.amount || 0) / 100.0;

    switch (tender.type) {
      case 'CARD':
        card += amount;
        break;
      case 'CASH':
        cash += amount;
        break;
      case 'SQUARE_GIFT_CARD':
        gift_card += amount;
        break;
      default:
        other += amount;
    }

    if (tender.processing_fee_money) {
      fees -= (tender.processing_fee_money.amount || 0) / 100.0;
    }
  }

  // Returns
  const return_amount = order.return_amounts?.total_money?.amount 
    ? Math.abs(order.return_amounts.total_money.amount) / 100.0 
    : 0;

  const net_total = cash + card + gift_card + other + fees;

  // Create detailed row
  detailedReports.push({
    json: {
      date: order_date,
      time: order_time,
      location_id,
      location_name,
      order_id,
      items: items_description,
      gross_sales: gross_sales.toFixed(2),
      total_discount: total_discount.toFixed(2),
      net_sales: net_sales.toFixed(2),
      total_tax: total_tax.toFixed(2),
      total_tip: total_tip.toFixed(2),
      total_money: total_money.toFixed(2),
      returns: return_amount.toFixed(2),
      cash: cash.toFixed(2),
      card: card.toFixed(2),
      gift_card: gift_card.toFixed(2),
      other_payment: other.toFixed(2),
      processing_fees: fees.toFixed(2),
      net_total: net_total.toFixed(2)
    }
  });
}

console.log(`Created ${detailedReports.length} detailed records`);

return detailedReports;
