// Get the single input item object directly
// In this mode, $input.item usually refers to the single item data structure
const item = $input.item;

// Check if the 'json' property and the 'output' property exist within the item
// AND ensure 'output' is actually a string before trying to modify it
if (item.json && typeof item.json.output === 'string') {

  // Read the text directly from item.json.output
  let messageText = item.json.output;

  // --- Regex Logic (remains the same) ---
  // ExpresiÃ³n regular para encontrar dominios despuÃ©s de '@'
  const emailDomainRegex = /@([a-zA-Z0-9.-]+\.[a-zA-Z]{2,})/g;

  // Reemplaza solo los dominios de correo electrÃ³nico
  messageText = messageText.replace(emailDomainRegex, (match, domain) => {
    // Inserta un espacio de ancho cero entre cada caracter del dominio
    const modifiedDomain = domain.split('').join('\u200B');
    return '@' + modifiedDomain;
  });
  // --- End of Regex Logic ---

  // Write the modified text back directly to item.json.output
  item.json.output = messageText;

} else {
  // Optional: Log a warning if the expected path wasn't found or wasn't a string
  if (!item.json) {
      console.log("Warning: Input item did not contain 'json' property.");
  } else if (typeof item.json.output !== 'string') {
      console.log("Warning: Input item's 'json.output' property was not a string. Value:", item.json.output);
  }
}

// Return the potentially modified item object itself
// This is what the next node will receive
return item;

// 1. Obtener el item combinado del nodo Merge
const mergedItem = $input.item.json;

// 2. Extraer la informaciÃ³n especÃ­fica directamente del item combinado
const fullMessage = mergedItem.output;
const destinatario = mergedItem.Destinatario;
const instancia = mergedItem.Instancia;
const originalMessageId = $input.first().json.messages[0].id

// 3. Dividir el mensaje usando la nueva expresiÃ³n regular
//    Divide por doble salto de lÃ­nea O por un salto de lÃ­nea seguido de "NÃºmero. "
const regex = /\n\n|\n(?=\d+\.\s)/;
const messageParts = fullMessage.split(regex);

// 4. Filtrar partes vacÃ­as (igual que antes)
const nonEmptyParts = messageParts.filter(part => part.trim() !== '');

// 5. Crear un nuevo item para cada parte del mensaje (igual que antes)
const outputItems = nonEmptyParts.map(part => {
  return {
    json: {
      Destinatario: destinatario,
      Instancia: instancia,
      originalMessageId: originalMessageId,
      messagePart: part.trim() // trim() sigue siendo Ãºtil aquÃ­
    }
  };
});

