(() => {
  const raw = String($node["[Supervisor]: An√°lisis de Sentimiento"].data.output.reporte_manager.cuerpo_mensaje ?? "");

  // 1) Escapar HTML b√°sico
  let s = raw
    .replace(/&/g,"&amp;")
    .replace(/</g,"&lt;")
    .replace(/>/g,"&gt;");

  // 2) Links y correos
  s = s
    .replace(/(https?:\/\/[^\s<]+)/g,'<a href="$1">$1</a>')
    .replace(/\b([A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,})\b/gi,'<a href="mailto:$1">$1</a>');

  // 3) T√≠tulos estilo Markdown (#, ##, ###)
  s = s
    .replace(/^\s*###\s+(.+)$/gm,'<h3 style="margin:12px 0 6px 0">$1</h3>')
    .replace(/^\s*##\s+(.+)$/gm,'<h2 style="margin:14px 0 8px 0">$1</h2>')
    .replace(/^\s*#\s+(.+)$/gm, '<h1 style="margin:16px 0 10px 0">$1</h1>');

  // 4) Negritas, it√°licas e inline code
  s = s
    .replace(/\*\*(.+?)\*\*/g,'<strong>$1</strong>')   // **bold**
    .replace(/__(.+?)__/g,'<strong>$1</strong>')       // __bold__
    .replace(/`(.+?)`/g,'<code style="font-family:ui-monospace,SFMono-Regular,Menlo,Consolas,monospace;padding:0 2px;border:1px solid #eee;border-radius:4px">$1</code>')
    .replace(/(^|[^\w*])\*(?!\s)([^*]+?)\*(?!\*)(?=[^\w*]|$)/g,'$1<em>$2</em>') // *italics*
    .replace(/(^|[^\w_])_(?!\s)([^_]+?)_(?=[^\w_]|$)/g,'$1<em>$2</em>');       // _italics_

  // 5) Citas: l√≠neas que comienzan con '>'
  s = s.replace(/^\s*>\s?(.+)$/gm,'<blockquote style="margin:8px 0;padding-left:10px;border-left:3px solid #ddd">$1</blockquote>');

  // 6) Listas con '-' o '*'
  s = s.replace(/(?:^|\n)([-*]\s+.+(?:\n[-*]\s+.+)*)/g, (m) => {
    const items = m.trim().split(/\n/).map(l => l.replace(/^[-*]\s+/, '').trim()).map(t => `<li>${t}</li>`).join('');
    return `\n<ul style="margin:8px 0 8px 20px;padding:0">${items}</ul>`;
  });

  // 7) Listas numeradas "1. ..."
  s = s.replace(/(?:^|\n)((?:\d+\.\s+.+\n?)+)/g, (m, g1) => {
    const items = g1.trim().split(/\n/).map(l => l.replace(/^\d+\.\s+/, '').trim()).map(t => `<li>${t}</li>`).join('');
    return `\n<ol style="margin:8px 0 8px 20px;padding:0">${items}</ol>`;
  });

  // 8) P√°rrafos y saltos de l√≠nea
  s = s
    .replace(/\r/g,'')
    .replace(/\n{2,}/g,'</p><p>')
    .replace(/\n/g,'<br>');

  // 9) Contenedor final para Gmail
  return '<div style="font-family:Inter,Segoe UI,Arial,sans-serif;font-size:14px;line-height:1.5"><p>' + s + '</p></div>';
})()
}}

