(() => {
  // 0) Entrada base
  const root = ($('[Supervisor]: An√°lisis de Sentimiento').dat?.json?.output) || {};
  let data = root.ticket_negativo;
  if (typeof data === 'string') { try { data = JSON.parse(data); } catch(e) {} }
  if (!data || typeof data !== 'object') data = {};

  // Aux: ¬øobjeto vac√≠o o solo valores nulos/vac√≠os?
  const isEmptyObj = (o) => {
    const ks = Object.keys(o);
    if (!ks.length) return true;
    return ks.every(k => o[k] === null || o[k] === undefined || (typeof o[k] === 'string' && o[k].trim() === ''));
  };

  // 1) Orden sugerido
  const prefer = [
    'titulo_ticket','asunto','resumen','descripcion_problema','historia_usuario',
    'criterios_aceptacion','sentimiento','intensidad','emociones',
    'departamento','prioridad','severidad','estado','cliente','contacto','email',
    'telefono','canal','agente','created_at','fecha','updated_at','causa',
    'acciones','siguientes_pasos','recomendaciones','tags','evidencias','enlaces'
  ];

  const seen = new Set();
  const out = [];
  out.push('=== Ticket Negativo ===');

  // 2) Utils
  const tt = (k) => k
    .replace(/[_\-]+/g,' ')
    .replace(/([a-z])([A-Z])/g,'$1 $2')
    .replace(/\s+/g,' ')
    .trim()
    .replace(/^./, c => c.toUpperCase());

  const isDateLike = (v) => typeof v === 'string' && (
    /^\d{4}-\d{2}-\d{2}(?:[T\s]\d{2}:\d{2}(?::\d{2}(?:\.\d{3})?)?Z?)?$/.test(v)
  );

  const fmtDate = (s) => {
    try {
      const d = new Date(s);
      if (!isNaN(d)) {
        return d.toLocaleString('es-CO', {
          dateStyle: 'medium',
          timeStyle: s.length > 10 ? 'short' : undefined,
          hour12: false
        });
      }
    } catch {}
    return s;
  };

  const maskPII = (str) => {
    let s = String(str);
    s = s.replace(/\b([A-Z0-9._%+-])([A-Z0-9._%+-]*)(@[A-Z0-9.-]+\.[A-Z]{2,})\b/gi,
                  (m,a,b,dom) => a + (b ? '***' : '') + dom);
    s = s.replace(/\+?\d[\d\s\-().]{6,}\d/g, (m) => {
      const chars = m.split('');
      let digitsSeen = 0;
      for (let i = chars.length - 1; i >= 0; i--) {
        if (/\d/.test(chars[i])) {
          digitsSeen++;
          if (digitsSeen > 4) chars[i] = '*';
        }
      }
      return chars.join('');
    });
    return s;
  };

  const fmtVal = (v, indent = 0) => {
    const pad = ' '.repeat(indent);
    if (v === null || v === undefined || v === '') return '‚Äî';
    if (typeof v === 'boolean') return v ? 'S√≠' : 'No';
    if (typeof v === 'number') return String(v);

    if (Array.isArray(v)) {
      if (!v.length) return '‚Äî';
      const lines = [];
      for (const it of v) {
        if (it && typeof it === 'object') {
          lines.push(pad + '‚Ä¢');
          lines.push(fmtObj(it, indent + 2));
        } else {
          const s = String(it).trim();
          lines.push(pad + '‚Ä¢ ' + maskPII(isDateLike(s) ? fmtDate(s) : s));
        }
      }
      return lines.join('\n');
    }

    if (typeof v === 'object') {
      return '\n' + fmtObj(v, indent + 2);
    }

    const s = String(v).trim();
    return maskPII(isDateLike(s) ? fmtDate(s) : s);
  };

  const fmtObj = (o, indent = 0) => {
    const pad = ' '.repeat(indent);
    const lines = [];
    for (const k of Object.keys(o)) {
      lines.push(pad + tt(k) + ': ' + fmtVal(o[k], indent + 2));
    }
    return lines.join('\n');
  };

  const pushKV = (k, v) => {
    if (v === undefined) return;
    seen.add(k);
    out.push(tt(k) + ': ' + fmtVal(v, 2));
  };

  // 3) Si ticket_negativo est√° vac√≠o, construir un resumen desde analisis_sentimiento + reporte_manager
  if (isEmptyObj(data)) {
    const an = root.analisis_sentimiento || {};
    const rm = root.reporte_manager || {};
    out.push('Asunto: ' + (rm.asunto || '‚Äî'));
    out.push('Sentimiento: ' + (an.sentimiento_general || '‚Äî'));
    out.push('Nivel de incidencia: ' + (an.nivel_de_incidencia || '‚Äî'));
    out.push('Emociones: ' + ((an.emociones_detectadas && an.emociones_detectadas.join(', ')) || '‚Äî'));
    out.push('Resumen: ' + (an.resumen_analisis || '‚Äî'));
    out.push('Departamento: ' + ((root.asignacion_interna && root.asignacion_interna.departamento_responsable) || '‚Äî'));
    if (rm.cuerpo_mensaje) {
      out.push('\nMensaje al manager:\n' + maskPII(String(rm.cuerpo_mensaje)));
    }
    out.push('\n‚Äî‚Äî Fin del ticket ‚Äî‚Äî');
    return out.join('\n');
  }

  // 4) Imprime primero las claves preferidas (sin usar prototype)
  const keysData = Object.keys(data);
  for (const k of prefer) {
    if (keysData.includes(k)) pushKV(k, data[k]);
  }
  // 5) Resto de claves
  for (const k of keysData.sort()) {
    if (!seen.has(k)) pushKV(k, data[k]);
  }

  out.push('\n‚Äî‚Äî Fin del ticket ‚Äî‚Äî');
  return out.join('\n');
})()
}}

